"use client"

import { useState, useEffect } from "react"
import "../styles/components.css"

export default function EventManagement({ activities, token }) {
  const [isModalOpen, setIsModalOpen] = useState(false)
  const [isLoading, setIsLoading] = useState(false)
  const [formData, setFormData] = useState({
    activityId: "",
    title: "",
    description: "",
    category: "",
    date: "",
    time: "",
    location: "",
    capacity: "",
    pointsPerEvent: 10,
  })

  const [createdEvents, setCreatedEvents] = useState([])

  useEffect(() => {
    fetchAllEvents()
  }, [])

  const fetchAllEvents = async () => {
    try {
      const allEvents = []
      for (const activity of activities) {
        const response = await fetch(`http://localhost:5000/api/events/activity/${activity._id}`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        if (response.ok) {
          const events = await response.json()
          allEvents.push(...events)
        }
      }
      setCreatedEvents(allEvents)
    } catch (error) {
      console.error("Error fetching events:", error)
    }
  }

  const handleInputChange = (e) => {
    const { name, value } = e.target
    setFormData({
      ...formData,
      [name]: name === "pointsPerEvent" ? Number.parseInt(value) : value,
    })
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setIsLoading(true)

    try {
      const response = await fetch("http://localhost:5000/api/events", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      })

      if (response.ok) {
        const eventData = await response.json()
        setCreatedEvents([eventData, ...createdEvents])

        setFormData({
          activityId: "",
          title: "",
          description: "",
          category: "",
          date: "",
          time: "",
          location: "",
          capacity: "",
          pointsPerEvent: 10,
        })
        setIsModalOpen(false)
        alert("Event created successfully")
      } else {
        const error = await response.json()
        alert(error.message || "Failed to create event")
      }
    } catch (error) {
      console.error("Error creating event:", error)
      alert("Error creating event")
    } finally {
      setIsLoading(false)
    }
  }

  const downloadRegistrationsCSV = (event) => {
    if (!event.attendees || event.attendees.length === 0) {
      alert("No registrations to export")
      return
    }

    const csvHeaders = ["Name", "Email", "Registration Date"]
    const csvRows = event.attendees.map((student) => {
      return [
        student.name || "N/A",
        student.email || "N/A",
        new Date().toLocaleDateString() // Using current date as reg date isn't explicitly stored in simple array
      ]
    })

    const csvContent = [csvHeaders.join(","), ...csvRows.map((row) => row.map((cell) => `"${cell}"`).join(","))].join(
      "\n",
    )

    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" })
    const link = document.createElement("a")
    const url = URL.createObjectURL(blob)

    link.setAttribute("href", url)
    link.setAttribute("download", `${event.title}_registrations_${new Date().toISOString().split("T")[0]}.csv`)
    link.style.visibility = "hidden"
    document.body.appendChild(link)
    link.click()
    document.body.removeChild(link)
  }

  return (
    <div className="management-container">
      <div className="management-header">
        <h2>Event Management</h2>
        <button className="btn-primary create-btn" onClick={() => setIsModalOpen(true)}>
          + Create New Event
        </button>
      </div>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Create New Event</h3>
              <button className="close-btn" onClick={() => setIsModalOpen(false)}>
                √ó
              </button>
            </div>

            <form onSubmit={handleSubmit} className="event-form">
              <div className="form-grid">
                <div className="form-group full-width">
                  <label>Select Activity</label>
                  <select
                    name="activityId"
                    value={formData.activityId}
                    onChange={handleInputChange}
                    required
                    className="form-control"
                  >
                    <option value="">Choose an activity</option>
                    {activities.map((activity) => (
                      <option key={activity._id} value={activity._id}>
                        {activity.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="form-group full-width">
                  <label>Event Title</label>
                  <input
                    type="text"
                    name="title"
                    value={formData.title}
                    onChange={handleInputChange}
                    required
                    className="form-control"
                    placeholder="e.g., Annual Tech Symposium"
                  />
                </div>

                <div className="form-group full-width">
                  <label>Category</label>
                  <select
                    name="category"
                    value={formData.category}
                    onChange={handleInputChange}
                    required
                    className="form-control"
                  >
                    <option value="">Select Category</option>
                    <option value="workshop">Workshop</option>
                    <option value="seminar">Seminar</option>
                    <option value="competition">Competition</option>
                    <option value="lecture">Lecture</option>
                    <option value="cultural">Cultural</option>
                    <option value="sports">Sports</option>
                    <option value="other">Other</option>
                  </select>
                </div>

                <div className="form-group full-width">
                  <label>Description</label>
                  <textarea
                    name="description"
                    value={formData.description}
                    onChange={handleInputChange}
                    className="form-control"
                    rows="3"
                    placeholder="Describe the event..."
                  />
                </div>

                <div className="form-group">
                  <label>Date</label>
                  <input
                    type="date"
                    name="date"
                    value={formData.date}
                    onChange={handleInputChange}
                    required
                    className="form-control"
                  />
                </div>

                <div className="form-group">
                  <label>Time</label>
                  <input
                    type="time"
                    name="time"
                    value={formData.time}
                    onChange={handleInputChange}
                    className="form-control"
                  />
                </div>

                <div className="form-group">
                  <label>Location</label>
                  <input
                    type="text"
                    name="location"
                    value={formData.location}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="e.g., Auditorium A"
                  />
                </div>

                <div className="form-group">
                  <label>Capacity</label>
                  <input
                    type="number"
                    name="capacity"
                    value={formData.capacity}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="Max attendees"
                  />
                </div>

                <div className="form-group full-width points-group">
                  <label>Points for Attendance</label>
                  <div className="points-input-wrapper">
                    <input
                      type="number"
                      name="pointsPerEvent"
                      value={formData.pointsPerEvent}
                      onChange={handleInputChange}
                      min="1"
                      className="form-control"
                    />
                    <span className="points-badge-preview">Points</span>
                  </div>
                  <p className="form-hint">Students will receive these points automatically upon attendance.</p>
                </div>
              </div>

              <div className="modal-actions">
                <button type="button" className="btn-secondary" onClick={() => setIsModalOpen(false)}>
                  Cancel
                </button>
                <button type="submit" className="btn-primary" disabled={isLoading}>
                  {isLoading ? "Creating..." : "Create Event"}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="events-list-section">
        <h3>All Events</h3>
        {createdEvents.length > 0 ? (
          <div className="events-list-grid">
            {createdEvents.map((event) => (
              <div key={event._id} className="event-card">
                <div className="event-info">
                  <div className="event-header-row">
                    <h3>{event.title}</h3>
                    {event.category && (
                      <span className="category-badge-small">{event.category}</span>
                    )}
                  </div>
                  <div className="event-meta">
                    <p className="event-date">
                      üìÖ {new Date(event.date).toLocaleDateString()} {event.time && `‚Ä¢ üïí ${event.time}`}
                    </p>
                    <p className="event-location">üìç {event.location || "TBA"}</p>
                  </div>
                  <div className="event-stats">
                    <span className="stat-badge points">üèÜ {event.pointsPerEvent} pts</span>
                    <span className="stat-badge capacity">üë• {event.attendees?.length || 0} / {event.capacity || "‚àû"}</span>
                  </div>

                  <div className="event-card-actions">
                    <button
                      className="btn-text-icon"
                      onClick={() => downloadRegistrationsCSV(event)}
                      title="Export Registrations"
                    >
                      üì• Export Data
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-state">
            <p>No events found. Create one to get started!</p>
          </div>
        )}
      </div>
    </div>
  )
}
